import React, {useState, Component} from "react";
import { variable } from "../service/api";

//const  DropList = () => {
    export class DropList extends Component{

    refreshList = () =>{
        fetch(variable.API_MANGAS +'mangas', {
            method:'GET',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            }
        })
        .then(response => response.json())
        .then(data => {
            console.log(data)
            this.setState({mangas:data})
        })
    }

    componentDidMount = () =>{
        this.refreshList();
    }

    render(){
    return(
        <div className="App">            
            <select>
            {variable.menuItems.map((i) =>{
               return <option>{i.title}</option>
            })}
            </select>
        </div>
    )
        }
}

export default DropList;
