import React,{Component} from "react";
import InputMask from 'react-input-mask';
import { variable } from "../service/api";

export class EntrMercadorias extends Component{

    constructor(props) {
        super(props);

        this.state={
            mercNome:"",
            mercFabri:"",
            mercTipo:"",
            descricao:"",
            local:""
        }
    }

    sendData(){
        if(this.state.mercNome == ""){
            alert("Favor informar o nome da Mercadoria");
        }else if(this.state.fabricant == ""){
            alert("Favor informar o Fabricante");
        }else if(this.state.mercTipo == ""){
            alert("Favor informar o tipo da mercadoria");
        }else if(this.state.local == ""){
            alert("Favor informar o local");
        }else{
            fetch('http://localhost:44306/api/EntrMercadoria/NovaMercadoria',{
                method:'POST',
                headers:{
                    'Accept':'application/json',
                    'Content-Type':'application/json'
                },
                body:JSON.stringify({
                    MERCAD_NOME:this.state.mercNome,
                    FABRICANTE:this.state.mercFabri,
                    TIPO:this.state.mercTipo,
                    DESCRICAO:this.state.descricao,
                    QUANTIDADE:this.state.quantidade,
                    LOCAL:this.state.local 
                })
            })
            .then(res=>res.json())
            .then((result)=>{
                alert(result);
                this.refreshList();
            },(error)=>{
                console.log(error)
                alert(`Ops !, alguma coisa deu errado ${error.message}`);

            })
        }
    }

    nomeHandler = (e) =>{
        this.setState({mercNome:e.target.value});
    }

    fabricantHandler = (e) =>{
        this.setState({mercFabri:e.target.value});
    }

    tipoHandler = (e) => {
        this.setState({mercTipo:e.target.value})
    }

    descriHandler = (e) => {
        this.setState({descricao:e.target.value})
    } 
    
    quantiHandler = (e) => {
        this.setState({quantidade:e.target.value})
    }

    localHandler = (e) => {
        this.setState({local:e.target.value})
    }
    
    render(){
        const {
            mercNome,
            mercFabri,
            mercTipo,
            descricao,    
            quantidade,
            local
        }=this.state;

        return(
       
            <main class="entForm">
                <form>
               
                <h1 class="h3 mb-3 fw-normal">Cadastrar nova mercadoria</h1>

                    <input  required type="text" value={mercNome} onChange={this.nomeHandler} class="form-control top" placeholder="Digite o nome da mercadoria" autocomplete="off" autofocus />
                    <input  required type="text" value={mercFabri} onChange={this.fabricantHandler} class="form-control top" placeholder="Digite o nome do fabricante" autocomplete="off" autofocus />
                    <input  required type="text" value={mercTipo} onChange={this.tipoHandler} class="form-control top" placeholder="Digite o tipo da mercadoria" autocomplete="off" autofocus />
                    <textarea  required type="text" value={descricao} onChange={this.descriHandler} class="form-control top" placeholder="Descrição" autocomplete="off" autofocus />
                    <input  required type="number" value={quantidade} onChange={this.quantiHandler} class="form-control top" placeholder="Quantidade" autocomplete="off" autofocus />
                    <input  required type="text" value={local} onChange={this.localHandler} class="form-control top" placeholder="Local" autocomplete="off" autofocus />

                    <button class="w-100 btn btn-lg btn-primary" onClick={()=>this.sendData()}>Cadastrar Mercadoria</button>
            
                </form>
            </main>
        )
    }
    
}
