import React,{Component, useState} from "react";
import { apiAcess } from "../service/api";
import DropList from "./dropListMerc";

export class SaidaMercadorias extends Component{

    //const [mercad, setMercad] = useState();

    constructor(props) {
        super(props);

        this.state={
            mercNome:"",
            mercQuanti:""
        }
    }


    sendData(){
        if(this.state.mercNome == ""){
            alert("Favor informar Mercadoria");
        }else if(this.state.mercQuanti == 0){
            alert("Favor informar o Fabricante");
        }else{
            fetch(apiAcess.API_SAI_MERC+'SaidaMercadorias',{
                method:'POST',
                headers:{
                    'Accept':'application/json',
                    'Content-Type':'application/json',
                    'Access-Control-Allow-Origin':'*',
                    'Access-Control-Allow-Methods':'GET,PUT,POST,DELETE',
                    'Access-Control-Allow-Headers':'Content-Type',
                    'Access-Control-Allow-Credentials':'true'
                },
                body:JSON.stringify({
                    MERCAD_NOME:this.state.mercNome,
                    QUANTIDADE:this.state.mercQuanti         
                })
            })
            .then(res=>res.json())
            .then((result)=>{
                console.log(result);
                this.refreshList();
            },(error)=>{           
                console.log(error)
            })
        }
    }

    nomeHandler = (e) =>{
        this.setState({mercNome:e.target.value});
    }

    quantHandler = (e) =>{
        this.setState({mercQuanti:e.target.value});
    }

    merdCardHandler = (e) =>{
        console.log(e.target.value)
        this.setState({mercNome:e.target.value});
    }
    
    render(){
        const {
            mercNome,
            mercQuanti
        }=this.state;

        return(
       
            <main class="entForm">
                <form>
               
                <h1 class="h3 mb-3 fw-normal">Saida de mercadoria</h1>
                        <DropList setMerc={merdCardHandler}/>
                        <input  required type="text" value={mercNome} onChange={this.nomeHandler} class="form-control top" placeholder="Digite o nome da mercadoria" autocomplete="off" autofocus />
                        <input  required type="number" value={mercQuanti} onChange={this.quantHandler} class="form-control top" placeholder="Quantidade" autocomplete="off" autofocus />
                        <button class="w-100 btn btn-lg btn-primary" onClick={()=>this.sendData()}>Cadastrar Mercadoria</button>
                
                </form>
            </main>
        )
    }
    
}
